def check_good_solution(index):
    solar_orbiter = _solar_orbiter_udp([tmin, tmax])  
    prob = pg.problem(solar_orbiter)
    x = good_solutions[index]
    print (prob.fitness(x)) 
    solar_orbiter.pretty(x)
    solar_orbiter.plot(x)
    solar_orbiter.plot_distance_and_flybys(x)

def print_good_solutions():
    for i in range(len(good_solutions)):
        solar_orbiter = _solar_orbiter_udp([tmin, tmax])  
        lambert_legs = []
        resos = []
        x = good_solutions[i]
        solar_orbiter._compute_dvs(x, lambert_legs, resos)
        y = solar_orbiter.fitness(x)
        resos = [str(resos[i]._resonance) for i in range(len(resos))]
        print (str(y), str(resos)) 
     
def optimize():   
    solar_orbiter = _solar_orbiter_udp([tmin, tmax])  
    prob = pg.problem(solar_orbiter)
    fprob = single_objective(prob)   
       
    # logger().info('solar orbiter' + ' de -> cmaes c++ smart retry')
    # ret = advretry.minimize(fprob.fun, bounds=fprob.bounds, num_retries = 60000, 
        # logger = logger(), optimizer=de_cma(1500))
    
    logger().info('solar orbiter' + ' BiteOpt parallel retry')
    ret = retry.minimize(fprob.fun, bounds=fprob.bounds, num_retries = 32000, 
                         logger = logger(), optimizer=Bite_cpp(100000, M=6))
    return ret

